PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "personal_access_tokens" ("id" integer primary key autoincrement not null, "tokenable_type" varchar not null, "tokenable_id" integer not null, "name" varchar not null, "token" varchar not null, "abilities" text, "last_used_at" datetime, "expires_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "username" varchar not null, "email" varchar not null, "password" varchar not null, "remember_token" varchar, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "recipes" ("id" integer primary key autoincrement not null, "title" varchar not null, "cooking_time" integer not null, "prep_time" integer not null, "servings" integer not null, "steps" text not null, "ingredients" text not null, "user_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "favorites" ("id" integer primary key autoincrement not null, "user_id" integer not null, "recipe_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade, foreign key("recipe_id") references "recipes"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "tags" ("id" integer primary key autoincrement not null, "name" varchar not null, "category" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "recipe_tag" ("id" integer primary key autoincrement not null, "recipe_id" integer not null, "tag_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("recipe_id") references "recipes"("id") on delete cascade, foreign key("tag_id") references "tags"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "images" ("id" integer primary key autoincrement not null, "file_path" varchar not null, "alt_text" varchar, "recipe_id" integer, "user_id" integer, "created_at" datetime, "updated_at" datetime, foreign key("recipe_id") references "recipes"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
DELETE FROM sqlite_sequence;
CREATE INDEX "personal_access_tokens_tokenable_type_tokenable_id_index" on "personal_access_tokens" ("tokenable_type", "tokenable_id");
CREATE UNIQUE INDEX "personal_access_tokens_token_unique" on "personal_access_tokens" ("token");
CREATE UNIQUE INDEX "users_username_unique" on "users" ("username");
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE INDEX "recipes_title_index" on "recipes" ("title");
CREATE UNIQUE INDEX "favorites_user_id_recipe_id_unique" on "favorites" ("user_id", "recipe_id");
CREATE UNIQUE INDEX "tags_name_unique" on "tags" ("name");
COMMIT;
